mapper.insert:
insert into MentoringOrder(pg, pay_method, merchant_uid, pay_name, amount, buyer_email, buyer_name, buyer_tel, buyer_postcode, buyer_addr)
values (#{pg}, #{pay_method}, #{merchant_uid}, #{pay_name}, #{amount}, #{buyer_email}, #{buyer_name}, #{buyer_tel}, #{buyer_postcode}, #{buyer_addr})

    select OrderID
    from MentoringOrder
    where
    pg=#{pg}
    and pay_method=#{pay_method}
    and merchant_uid=#{merchant_uid}
    and pay_name=#{pay_name}
    and amount=#{amount}
    and buyer_email=#{buyer_email}
    and buyer_name=#{buyer_name}
    and buyer_tel=#{buyer_tel}
    and buyer_postcode=#{buyer_postcode}
    and buyer_addr=#{buyer_addr}
